const path = require('path');
module.exports = function(RED) {
    function ContextInitNode(n) {
        RED.nodes.createNode(this,n);
        this.template = n.template;
        var node = this;
    }

    RED.nodes.registerType("context-init",ContextInitNode);

    RED.httpAdmin.get("/context/editor/*",function(req,res) {
        var options = {
            root: path.join(__dirname,"lib","codeEditor"),
            dotfiles: 'deny'
        };
        res.sendFile(req.params[0], options);
    });
}