ace.define("ace/mode/bp_highlight_rules", ["require", "exports", "module", "ace/lib/oop", "ace/mode/javascript_highlight_rules"], function(require, exports, module) {
  "use strict";

  var oop = require("ace/lib/oop");
  var JavaScriptHighlightRules = require("ace/mode/javascript_highlight_rules").JavaScriptHighlightRules;

  var BpHighlightRules = function() {
    JavaScriptHighlightRules.call(this);

    // Add custom highlight rules here
    this.$rules.start.unshift({
      token: "variable.language",
      regex: "\\b(?:ctx|bp)\\b"
    });
  };

  oop.inherits(BpHighlightRules, JavaScriptHighlightRules);

  exports.BpHighlightRules = BpHighlightRules;
});
